import React from 'react';
import { useEffect } from 'react';
import { useState } from 'react';
import axios from 'axios';
import { Link } from "react-router-dom";


const Arts = () => {
    const [arts,setArts] = useState([])

    useEffect(() => {
        const fetchAllArts = async ()=>{
            try{
                const res = await axios.get("http://localhost:8800/arts")
                setArts(res.data);
            }catch (err) {
                console.log(err)
            }
}  ;
fetchAllArts();
}, []);

console.log(arts);

const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:8800/arts/${id}`);
      window.location.reload()
    } catch (err) {
      console.log(err);
    }
  };

    return (
        <div>
<h1>Wow Arts. com  - </h1> 
<div className="arts"> 
{arts.map((art) => (
<div className="art" key={art.id}> 
{art.cover && <img src={art.cover} alt="" />} 
<h2>{art.title}</h2>
<p>{art.description}</p>
<span>{art.price}</span>
<button className="delete">Delete</button>
<button className="update">Update</button>
        </div>
))}
</div>
<button>
   <Link to="/add" >Add new Art</Link>
    </button>
</div>
    );
};
export default Arts;
