import React from 'react';
import { useEffect } from 'react';
import { useState } from 'react';
import axios from 'axios';

const Arts = () => {
    const [arts,setArts] = useState([])

    useEffect(()=>{
        const fetchAllArts = async ()=>{
            try{
                const res = await axios.get("http://localhost:8800/arts")
                setArts(res.data);
            }catch(err){
                console.log(err)
            }
}  
fetchAllArts()
}, [])

    return (
        <div>
<h1>Wow Arts.com</h1> 
<div className="arts"> 
{arts.map((art) => (
<div className="art" key={art.id}> 
{art.cover && <img src={art.cover} alt=""/>} 
<h2>{art.title}</h2>
<p>{art.desc}</p>
<span>{art.price}</span>
        </div>
))}
</div>
</div>
    )
}
export default Arts
